import React, { useState } from 'react';
import Note from './Note';
import NotePopup from './NotePopup';

const NoteList = () => {
    const [notes, setNotes] = useState([{ title: 'Sample Note', content: 'This is a sample note.' }]);
    const [selectedNote, setSelectedNote] = useState(null);

    const openPopup = (note) => {
        setSelectedNote(note);
    };

    const closePopup = () => {
        setSelectedNote(null);
    };

    const saveNote = (updatedNote) => {
        setNotes((prevNotes) =>
            prevNotes.map((note) => (note === selectedNote ? updatedNote : note))
        );
        closePopup();
    };

    return (
        <div className="note-list">
            {notes.map((note, index) => (
                <Note key={index} note={note} onClick={() => openPopup(note)} />
            ))}
            {selectedNote && (
                <NotePopup
                    note={selectedNote}
                    onClose={closePopup}
                    onSave={saveNote}
                />
            )}
        </div>
    );
};

export default NoteList;
