import React, { useState, useEffect } from "react";
import { getNotes, createNote, deleteNote } from "./services/api";
import NoteList from "./components/NoteList";
import AddNoteForm from "./components/AddNoteForm";
import "./styles.css"; // Import styles
import { darken } from "polished";
import tinycolor from "tinycolor2";

// const determineColor = (color) => {
//   const isDark = tinycolor(color).isDark();
//   if (isDark) {
//     return darken(-0.2, color);
//   } else {
//     return darken(0.2, color);
//   }
// };

const App = () => {
  const [notes, setNotes] = useState([]);
  const [selectedNote, setSelectedNote] = useState(null); // Track the selected note for editing
  const [hovered, setHovered] = useState(false);
  const [hover, setHover] = useState(false);

  useEffect(() => {
    const fetchNotes = async () => {
      const data = await getNotes();
      setNotes(data);
    };
    fetchNotes();
  }, []);

  const handleAddNote = async (note) => {
    const newNote = await createNote(note);
    setNotes((prevNotes) => [...prevNotes, newNote]);
  };

  const handleDeleteNote = async (id) => {
    await deleteNote(id);
    setNotes((prevNotes) => prevNotes.filter((note) => note.id !== id));
  };

  const handleSelectNote = (note) => {
    setSelectedNote(note); // Open the note in edit mode
  };

  const handleClosePopup = () => {
    setSelectedNote(null); // Close the popup
  };

  const handleUpdateNote = (updatedNote) => {
    setNotes((prevNotes) =>
      prevNotes.map((note) => (note.id === updatedNote.id ? updatedNote : note))
    );
    setSelectedNote(null); // Close the popup after updating
  };

  return (
    <div>
      <div className="header">
        <h1>Sticky Notes</h1>
      </div>
      <div className="app">
        <AddNoteForm onAdd={handleAddNote} />
      </div>
      <div className="app notes">
        <NoteList
          notes={notes}
          onDelete={handleDeleteNote}
          onSelect={handleSelectNote} // Pass the select handler
        />
      </div>
      {selectedNote && (
        <div className="popup">
          <h3>Edit Note</h3>
          <input
            type="text"
            value={selectedNote.title}
            onChange={(e) =>
              setSelectedNote({ ...selectedNote, title: e.target.value })
            }
          />
          <textarea
            value={selectedNote.content}
            onChange={(e) =>
              setSelectedNote({ ...selectedNote, content: e.target.value })
            }
          />
          <button
            style={{
              backgroundColor: hovered
                ? darken(0.2, selectedNote.color)
                : selectedNote.color || "#007bff",
            }}
            onMouseEnter={() => setHovered(true)}
            onMouseLeave={() => setHovered(false)}
            onClick={() => handleUpdateNote(selectedNote)}
          >
            Save
          </button>
          <button
            style={{
              backgroundColor: hover
                ? darken(0.2, selectedNote.color)
                : selectedNote.color || "#007bff",
            }}
            onMouseEnter={() => setHover(true)}
            onMouseLeave={() => setHover(false)}
            onClick={handleClosePopup}
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
