import axios from "axios";

const API_URL = "http://localhost:5000";

const getAuthHeaders = () => {
  const token = localStorage.getItem("token");
  return {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };
};

// Define the login function
export const login = async (username, password) => {
  try {
    const response = await axios.post(`${API_URL}/login`, {
      username,
      password,
    }); // Send as JSON
    return response.data; // Assuming the backend sends back a token or user data
  } catch (error) {
    console.error("Login failed:", error);
    throw error;
  }
};


export const getNotes = async () => {
  const response = await axios.get(`${API_URL}/notes`, getAuthHeaders());
  return response.data;
};

export const createNote = async (note) => {
  const response = await axios.post(`${API_URL}/notes`, note, getAuthHeaders());
  return response.data;
};

export const deleteNote = async (id) => {
  await axios.delete(`${API_URL}/notes/${id}`, getAuthHeaders());
};

export const updateNote = async (note) => {
  const response = await axios.put(
    `${API_URL}/notes/${note.id}`,
    note,
    getAuthHeaders()
  );
  return response.data;
};
