import React from "react";
import { darken } from "polished";

const darkenColor = (color, amount) => {
  if (color === "#fff" || color === "#ffffff") {
    amount = 0.2;

    let col = color.startsWith("#") ? color.slice(1) : color;
    let num = parseInt(col, 16);

    let r = Math.max(0, (num >> 16) - amount);
    let g = Math.max(0, ((num >> 8) & 0x00ff) - amount);
    let b = Math.max(0, (num & 0x0000ff) - amount);

    return `#${((r << 16) | (g << 8) | b).toString(16).padStart(6, "0")}`;
  } else {
    return darken(-amount, color);
  }
};

const Note = ({ note, onDelete, onSelect }) => (
  <div
    className="note"
    style={{
      backgroundColor: note.color || "#fff", // Default color if none provided
      padding: "15px",
      margin: "10px",
      borderRadius: "8px",
      boxShadow: "0 2px 5px rgba(0, 0, 0, 0.2)",
      transition: "transform 0.2s",
      cursor: "pointer",
      overflow: "hidden", // Hide overflowing content
      textOverflow: "ellipsis", // Add ellipsis for long text
      whiteSpace: "nowrap", // Prevent text from wrapping
    }}
    onClick={() => onSelect(note)} // Open the note in edit mode
  >
    <h3>{note.title}</h3>
    <p style={{ overflowWrap: "break-word" }}>{note.content}</p>
    <button
      className="delete-button"
      style={{
        backgroundColor: note.color ? darkenColor(note.color, 30) : "#fff",
      }}
      onClick={(e) => {
        e.stopPropagation(); // Prevent triggering the onClick of the parent
        onDelete(note.id);
      }}
    >
      Delete
    </button>
  </div>
);

export default Note;
