import React, { useState, useRef, useEffect } from "react";

const AddNoteForm = ({ onAdd }) => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [color, setColor] = useState("#ffffff");
  const [isExpanded, setIsExpanded] = useState(false);
  const [initialHeight, setInitialHeight] = useState("auto");

  const titleInputRef = useRef(null);

  // Dynamically calculate the height of the title input
  useEffect(() => {
    if (titleInputRef.current) {
      setInitialHeight(`${titleInputRef.current.offsetHeight + 20}px`); // Add padding to match the container
    }
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd({ title, content, color });
    setTitle("");
    setContent("");
    setColor("#ffffff");
    setIsExpanded(false);
  };

  const handleExpand = () => {
    setIsExpanded(true);
  };

  return (
    <div
      className={`add-note-form ${isExpanded ? "expanded" : ""}`}
      onClick={!isExpanded ? handleExpand : undefined}
      style={{
        cursor: !isExpanded ? "pointer" : "default",
        height: isExpanded ? "auto" : initialHeight,
        padding: isExpanded ? "10px" : "0px 10px",
        border: "1px solid #e0e0e0",
        borderRadius: "8px",
        boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
        transition: "all 0.3s ease",
        overflow: "hidden",
      }}
    >
      <form
        onSubmit={handleSubmit}
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          width: "100%",
        }}
      >
        {/* Title Input */}
        <input
          ref={titleInputRef}
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{
            margin: "0",
            padding: "10px",
            borderRadius: "8px",
            width: "100%",
            height: "100%", // Ensure it fills the parent div
            border: "1px solid #e0e0e0",
            boxSizing: "content-box", // Include padding in width/height calculations
          }}
        />
        {/* Expanded Content */}
        {isExpanded && (
          <>
            <textarea
              placeholder="Content"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              style={{
                margin: "10px 0",
                padding: "10px",
                borderRadius: "8px",
                width: "100%",
                border: "1px solid #e0e0e0",
              }}
            />
            <input
              type="color"
              value={color}
              onChange={(e) => setColor(e.target.value)}
              style={{
                margin: "10px 0",
                padding: "10px",
                borderRadius: "8px",
                width: "100%",
                border: "1px solid #e0e0e0",
              }}
            />
            <button
              type="submit"
              style={{
                margin: "10px 0",
                padding: "10px",
                borderRadius: "8px",
                width: "100%",
                backgroundColor: "#007BFF",
                color: "#fff",
                border: "none",
                cursor: "pointer",
              }}
            >
              Add Note
            </button>
          </>
        )}
      </form>
    </div>
  );
};

export default AddNoteForm;
