import React, { useState } from "react";

const AddNoteForm = ({ onAdd }) => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [color, setColor] = useState("#ffffff");
  const [isExpanded, setIsExpanded] = useState(false); // State to track if the form is expanded

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd({ title, content, color });
    setTitle("");
    setContent("");
    setColor("#ffffff");
    setIsExpanded(false); // Collapse the form after submission
  };

  const handleExpand = () => {
    setIsExpanded(true);
  };

  return (
    <div
      className={`add-note-form ${isExpanded ? "expanded" : ""}`}
      onClick={!isExpanded ? handleExpand : undefined} // Expand on click if not already expanded
      style={{
        height: isExpanded ? "auto" : "50px", // Adjust height based on expansion state
        width: isExpanded ? "100%" : "300px", // Adjust width based on expansion state
        border: "1px solid #e0e0e0",
        borderRadius: "8px",
        boxShadow: "0 2px 5px rgba(0, 0, 0, 0.1)",
        padding: isExpanded ? "20px" : "0 10px", // Add padding when expanded
        overflow: "hidden",
        cursor: !isExpanded ? "pointer" : "default",
        transition: "all 0.3s ease",
      }}
    >
      <form
        onSubmit={handleSubmit}
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          width: "100%",
        }}
      >
        {/* Always visible input */}
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{
            margin: "10px 0",
            padding: "10px",
            borderRadius: "8px",
            width: "100%",
            border: "1px solid #e0e0e0",
          }}
        />
        {/* Conditionally render the rest of the form */}
        {isExpanded && (
          <>
            <textarea
              placeholder="Content"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              style={{
                margin: "10px 0",
                padding: "10px",
                borderRadius: "8px",
                width: "100%",
                border: "1px solid #e0e0e0",
              }}
            />
            <input
              type="color"
              value={color}
              onChange={(e) => setColor(e.target.value)}
              style={{
                margin: "10px 0",
                padding: "10px",
                borderRadius: "8px",
                width: "100%",
                border: "1px solid #e0e0e0",
              }}
            />
            <button
              type="submit"
              style={{
                margin: "10px 0",
                padding: "10px",
                borderRadius: "8px",
                width: "100%",
                backgroundColor: "#007BFF",
                color: "#fff",
                border: "none",
                cursor: "pointer",
              }}
            >
              Add Note
            </button>
          </>
        )}
      </form>
    </div>
  );
};

export default AddNoteForm;
