import React from "react";
import { darken } from "polished";
import tinycolor from "tinycolor2";

// Function to determine the appropriate background color for the delete button
const determineColor = (color) => {
  const isDark = tinycolor(color).isDark();
  return isDark ? darken(-0.2, color) : darken(0.2, color);
};

const Note = ({ note, onDelete, onSelect }) => {
  // Check if the note has large content
  const isLargeContent = note.content.length > 200;

  return (
    <div
      className={`note ${isLargeContent ? "large-content" : "small-content"}`} // Add class for large or small content
      style={{
        backgroundColor: note.color || "#fff",
        padding: "15px",
        margin: "10px",
        borderRadius: "8px",
        boxShadow: "0 2px 5px rgba(0, 0, 0, 0.2)",
        transition: "transform 0.2s",
        cursor: "pointer",
        overflowWrap: "break-word",
        wordBreak: "break-word",
        whiteSpace: "normal",
      }}
      onClick={() => onSelect(note)}
    >
      <h3>{note.title}</h3>
      <p>{note.content}</p>
      <button
        className="delete-button"
        style={{
          backgroundColor: note.color ? determineColor(note.color) : "#fff",
          border: "none",
          padding: "5px 10px",
          borderRadius: "4px",
          cursor: "pointer",
        }}
        onClick={(e) => {
          e.stopPropagation(); // Prevent triggering the parent div click
          onDelete(note.id);
        }}
      >
        Delete
      </button>
    </div>
  );
};

export default Note;
